import os


def is_tracing_enabled() -> bool:
    return (os.getenv("APMPLUS_TRACING_ENABLED") or "true").lower() == "true"


def is_content_tracing_enabled() -> bool:
    return (os.getenv("APMPLUS_TRACE_CONTENT") or "true").lower() == "true"


def is_metrics_enabled() -> bool:
    return (os.getenv("APMPLUS_METRICS_ENABLED") or "true").lower() == "true"

def is_logs_enabled() -> bool:
    return (os.getenv("APMPLUS_LOGS_ENABLED") or "true").lower() == "true"
